import React, { Component } from 'react';
import './Download.css';
import axios from 'axios';

class Download extends Component {
    constructor(props) {
        super(props)

        this.state = {
            filesInDB: false,
            allFilesAsText: [],
            allFilesName: []
        }
        this.displyFilesList = this.displyFilesList.bind(this);
    }


    sendRequestToGetFilesNames() {
       axios.get("http://localhost:8000/download")
       .then(res => {
           let allNamesFromDB = [];
           allNamesFromDB = res.data.names;
           let namesArray = [];
           allNamesFromDB.map((item) => {
               return(
                 namesArray.push(item)
               );
           })
           console.log(namesArray);
           this.setState({allFilesName: allNamesFromDB});
           console.log(allNamesFromDB);
       }).catch(err => console.log(err));

    }



    displyFilesList() {
      this.sendRequestToGetFilesNames();

    }




    render() {
        return (
            <button
                onClick={this.displyFilesList}
            >
                Files From DB
          </button>
        );
    }
}

export default Download
