import React, { Component } from 'react'
import './App.css'
import Upload from './upload/Upload'
import Download from './download/Download';
import TextFieldComp from './textField/TextFieldComp';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      textToTextField:''
    }

  }

insertTextToTextField(text){
  this.setState({
    textToTextField:text
  })
}


  render() {
    return (
      <div className="App">
        <div className="app-header">
          <span>Exam Trainer</span>
        </div>
        <div className="body">
          <div>
            <TextFieldComp fileText={this.state.text}/>
          </div>
          <div className="Card">
            <Upload />
          </div>
          <div className="DownloadCard">
            <Download callbackWithText={this.insertTextToTextField.bind(this)} />
          </div>
        </div>
        <div className="footer">
        </div>
      </div>
    )
  }
}

export default App;
