import React, { Component } from 'react'
import './TextFieldComp.css'


class TextFieldComp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            fileName: 'textttt',
            fileText: '',
            holdFileText: false
        }
    }

    componentDidMount(props){
        this.setState({
            fileText:props.fileText
        })
    }

    render() {

        return (
            <form >
                <label  htmlFor="component-outlined">
                    {this.state.fileName}
                </label>
                <textarea 
                    id="component-outlined"
                    value={this.state.fileText}
                />
            </form>

        );
    }
}
export default TextFieldComp;
