import React, { Component } from 'react';
import './Download.css';
import axios from 'axios';

class Download extends Component {
    constructor(props) {
        super(props)

        this.state = {
            filesInDB: false,
            allFilesAsText: [],
            allFilesName: []
        }
        this.displyFilesList = this.displyFilesList.bind(this);
    }


    sendRequestToGetFilesNames() {
       axios.post("http://localhost:8000/download")
       .then(res => {
           this.setState({allFilesName: res.data});
       }).catch(err => console.log(err));

    }



    displyFilesList() {
      this.sendRequestToGetFilesNames();
      
    }




    render() {
        return (
            <button
                onClick={this.displyFilesList}
            >
                Files From DB
          </button>
        );
    }
}

export default Download
