import React, { Component } from 'react';
import './Download.css';
import axios from 'axios';

class Download extends Component {
    constructor(props) {
        super(props)

        this.state = {
            filesInDB: false,
            allFilesAsText: [],
            allFilesName: []
        }
        this.displyFilesList = this.displyFilesList.bind(this);
    }


    sendRequestToGetFilesNames() {
       axios.get("http://localhost:8000/download")
       .then(res => {
           const allNamesFromDB = res.data.names;
           const namesArray = [];
           allNamesFromDB.map((item) => {
               return(
                 namesArray.push(item.name)
               );
           })
           console.log(namesArray);
           this.setState({allFilesName: namesArray});
           console.log(allNamesFromDB);
       }).catch(err => console.log(err));

    }



    displyFilesList() {
      this.sendRequestToGetFilesNames();

    }
    render() {
        let allNames = this.state.allFilesName.map((item, i) => {
            return (
              <li 
                data-id={i}
                key={i}>{item}</li>
            )
        }); 
        return (
            <div>
            <button
                onClick={this.displyFilesList}>
                Files From DB
          </button>
          <div>
          <ul>{allNames}</ul>
          </div>
          </div>
        );
    }
}

export default Download
