import React, { Component } from 'react';
import './Download.css';
import axios from 'axios';

class Download extends Component {
    constructor(props) {
        super(props)

        this.state = {
            filesInDB: false,
            allFilesAsText: [],
            allFilesName: [],
            fileTextThatClicked: ''
        }
        this.displyFilesList = this.displyFilesList.bind(this);
        this.sendRequestToGetFileText = this.sendRequestToGetFileText.bind(this);
        this.sendRequestToGetFilesNames = this.sendRequestToGetFilesNames.bind(this);
        this.getAllTextClick = this.getAllTextClick.bind(this);
    }


    sendRequestToGetFilesNames() {
        axios.get("http://localhost:8000/download?getOnlyNames=1")
            .then(res => {
                const allNamesFromDB = res.data.names;
                let namesArray = allNamesFromDB.map(item => { return item });
                console.log(allNamesFromDB);
                console.log(namesArray);
                this.setState({ allFilesName: namesArray });

            }).catch(err => console.log(err));

    }

    sendRequestToGetFileText(fileID) {
        axios.get("http://localhost:8000/download/", {
            params: {
                id: fileID
            }
        })
            .then(res => {
                console.log(res.data.text);
                this.setState({ fileTextThatClicked: res.data.text});
            }).catch(err => console.log(err));
    }


    getAllTextClick(fileID) {
        this.sendRequestToGetFileText(fileID);
    }


    displyFilesList() {
        this.sendRequestToGetFilesNames();
        //this.props.callbackWithText(this.state.fileTextThatClicked);
    }


    render() {
        let allNames = this.state.allFilesName.map((item, i) => {
            return (
                <li onClick = {this.getAllTextClick(item._id)}
                    data-id={i}
                    key={item._id}>{item.name}    
                </li>
            )
        });
        return (
            <div>
                <button
                    onClick={this.displyFilesList}>
                    Files From DB
          </button>
                <ul>{allNames}</ul>
            </div>
        );
    }
}

export default Download
